// @ts-nocheck
class SaturnIntegration {
  init(user: any) {
    console.log("user data ::", user);

    if (!user) {
      window.removeEventListener("saturn:ready", () => {});
      // @ts-ignore
      window?.$saturn?.reset?.();
      return;
    }

    console.log("Saturn Integration Start");
    // @ts-ignore
    console.log("window", window.$saturn);
    const userData = user;

    // @ts-ignore
    setTimeout(() => {
      console.log("calling setuser", userData);
      if (window?.$saturn && window?.$saturn?.isLoaded) {
        console.log("calling setuser", userData);
        window.$saturn.isUserLoaded = false;
        // @ts-ignore
        window.$saturn.setUser(userData?.uid, {
          email: userData?.email,
          name: userData?.name,
        });
      } else {
        window.addEventListener(
          "saturn:ready",
          function () {
            if (userData) {
              // @ts-ignore
              window.$saturn.setUser(userData.uid, {
                email: userData.email,
                name: userData.name,
              });
            }
          },
          { once: true }
        );
      }
    }, 1000);
  }

  reset() {
    // @ts-ignore
    if (window?.$saturn && window?.$saturn?.isLoaded) {
      // @ts-ignore
      window?.$saturn?.reset?.();
    }
  }
}

export const saturnIntegration = new SaturnIntegration();
