class SaturnIntegration {
  init(user: any) {
    if (!user) {
      window.removeEventListener("saturn:ready", () => {});
      // @ts-ignore
      window?.$saturn?.reset?.();
      return;
    }

    console.log("Saturn Integration Start");

    const userData = user;
    // @ts-ignore
    if (window?.$saturn && window?.$saturn?.isLoaded) {
      // @ts-ignore
      window.$saturn.setUser(userData?.uid, {
        email: userData?.email,
        name: userData?.name,
      });
    } else {
      window.addEventListener(
        "saturn:ready",
        function () {
          if (userData) {
            // @ts-ignore
            window.$saturn.setUser(userData.uid, {
              email: userData.email,
              name: userData.name,
            });
          }
        },
        { once: true }
      );
    }
  }

  reset() {
    // @ts-ignore
    if (window?.$saturn && window?.$saturn?.isLoaded) {
      // @ts-ignore
      window?.$saturn?.reset?.();
    } else {
      window.addEventListener(
        "saturn:ready",
        () => {
          // @ts-ignore
          window?.$saturn?.reset?.();
        },
        { once: true }
      );
    }
  }
}

export const saturnIntegration = new SaturnIntegration();
